import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ProductService } from '../services/product.service';

interface Product {
  name: string;
  brand?: string;
  shade?: string;
  price?: string;
  image?: string;
  link?: string;
}

@Component({
  selector: 'app-results',
  templateUrl: './results.component.html',
  styleUrls: ['./results.component.css'],
})
export class ResultsComponent implements OnInit {
  products: Product[] = [];
  loading: boolean = true;
  error: string = '';
  noData: boolean = false;

  constructor(private productService: ProductService, private router: Router) {}

  ngOnInit(): void {
    // טוען מוצרים שנשמרו בשירות
    const cachedProducts = this.productService.getProducts();

    if (cachedProducts && cachedProducts.length > 0) {
      this.products = cachedProducts;
      this.loading = false;
    } else {
      this.loading = false;
      this.noData = true;
    }
  }

  goBackToQuiz(): void {
    this.router.navigate(['/quiz']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Results Page</title>
  <link rel="stylesheet" href="results.component.css">
</head>
<body>
<header>
  <a [routerLink]="['/']">
      <img src="assets/logo.jpg" alt="Logo" class="logo">
  </a>
</header>
<main>
  <div class="container">
    <h1>Your Personalized Makeup Matches</h1>
    <p>Here are the top products just for you!</p>

    <div *ngIf="loading">Loading products...</div>
    <div *ngIf="error">{{ error }}</div>

    <!-- כרטיסי התוצאות -->
    <div class="results-container" *ngIf="!loading && products.length > 0">
      <div *ngFor="let product of products" class="result-item">
        <div class="result-content">
          <h3 class="result-title">{{ product.name }}</h3>
          <p *ngIf="product.brand">{{ product.brand }}</p>
          <p *ngIf="product.shade">{{ product.shade }}</p>
          <p class="result-price">{{ product.price }}</p>
        </div>
        <a [href]="product.link" target="_blank" class="buy-button">Buy Now</a>
      </div>
    </div>

    <!-- הודעה אם אין מוצרים -->
    <div *ngIf="!loading && products.length === 0">
      No products found.
    </div>

    <button (click)="goBackToQuiz()">Back to Quiz</button>
  </div>
</main>
</body>
</html>
