import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { finalize } from 'rxjs/operators';
import { ProductService } from '../services/product.service';

interface QuestionSpec {
  text: string;
  options?: string[];
}

const productQuestionsMap: Record<string, QuestionSpec[]> = {
  mascara: [
    { text: 'Is it waterproof?', options: ['Yes', 'No'] },
    { text: 'Color', options: ['Black', 'Brown', 'Blue'] },
    { text: 'Longevity', options: ['Low', 'Medium', 'High'] },
    { text: 'Purpose', options: ['Volume', 'Length', 'Curl'] },
  ],
  eyeshadow: [
    { text: 'Longevity', options: ['Low', 'Medium', 'High'] },
    { text: 'Eye Color', options: ['Brown', 'Blue', 'Green', 'Hazel'] },
    { text: 'Skin Tone', options: ['Fair', 'Light', 'Medium', 'Tan', 'Deep'] },
    { text: 'Preferred Palette', options: ['Neutrals', 'Warm', 'Cool', 'Smokey', 'Colorful'] },
  ],
  lips: [
    { text: 'Longwear?', options: ['Yes', 'No'] },
    { text: 'Finish', options: ['Matte', 'Gloss'] },
    { text: 'Format', options: ['Lipstick', 'Lip Gloss'] },
    { text: 'Preferred Palette', options: ['Nudes', 'Pinks', 'Reds', 'Corals', 'Berries'] },
    { text: 'Skin Tone', options: ['Fair', 'Light', 'Medium', 'Tan', 'Deep'] },
  ],
  eyebrow: [
    { text: 'Product Type', options: ['Pencil', 'Marker', 'Gel'] },
    { text: 'Hair Color', options: ['Blonde', 'Light Brown', 'Brown', 'Dark Brown', 'Black', 'Ginger'] },
    { text: 'Styling Preference', options: ['Brow Mascara', 'Gel', 'Soap', 'Wax'] },
  ],
  eyeliner: [
    { text: 'Formula Type', options: ['Liquid', 'Pen (Felt-tip)', 'Gel'] },
  ],
  primer: [
    // אין שאלות – מבוסס על skinType שהגיע מה־Quiz
  ],
};

@Component({
  selector: 'app-product-questions',
  templateUrl: './product-questions.component.html',
  styleUrls: ['./product-questions.component.css'],
})
export class ProductQuestionsComponent implements OnInit {
  selectedProduct: string = '';
  questions: QuestionSpec[] = [];
  answers: string[] = [];
  loading: boolean = false;
  skinTypeFromQuiz: string | null = null;

  constructor(
    private productService: ProductService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    // קבלת product + skinType מה־query params
    this.route.queryParamMap.subscribe((params) => {
      this.selectedProduct = (params.get('product') || '').toLowerCase();
      this.skinTypeFromQuiz = params.get('skinType');

      if (!this.selectedProduct) {
        alert('No product selected! Please go back and choose a product.');
      } else {
        this.loadQuestions(this.selectedProduct);
      }
    });
  }

  loadQuestions(product: string): void {
    const questions = productQuestionsMap[product];
    if (!questions) {
      alert('No questions available for this product.');
      this.questions = [];
      return;
    }
    this.questions = questions;
    this.answers = Array(this.questions.length).fill('');
  }

  onGetRecommendations(): void {
    // Primer: אין שאלות, מבוסס על skinType
    if (this.selectedProduct !== 'primer' && this.answers.some((a) => !a)) {
      alert('Please answer all questions before submitting.');
      return;
    }

    const payload = {
      productType: this.selectedProduct,
      answers: this.selectedProduct === 'primer' ? { skinType: this.skinTypeFromQuiz } : this.answers,
    };

    this.loading = true;

    this.productService
      .getMlProducts(payload)
      .pipe(finalize(() => (this.loading = false)))
      .subscribe({
        next: (products: any[]) => {
          if (!products || products.length === 0) {
            alert('No products found for your choices.');
            return;
          }
          this.productService.setProducts(products); // לשימוש ב־ResultsComponent
          this.router.navigate(['/results']);
        },
        error: (err: unknown) => {
          console.error('Failed to get products:', err);
          const msg =
            (err as any)?.error?.error ||
            (err as any)?.error?.message ||
            (err as any)?.message ||
            'Failed to load products';
          alert(msg);
        },
      });
  }
}
<header class="header">
  <a routerLink="/">
    <img src="assets/logo.jpg" alt="Logo" class="logo" />
  </a>
</header>

<div class="container">
  <h2>Questions for {{ selectedProduct | titlecase }}</h2>

  <!-- Primer: אין שאלות נוספות -->
  <div *ngIf="selectedProduct === 'primer'">
    <p>
      Primer is adapted automatically according to your skin type from the quiz.
    </p>
    <p *ngIf="skinTypeFromQuiz">
      Detected skin type: <strong>{{ skinTypeFromQuiz }}</strong>
    </p>
    <p *ngIf="!skinTypeFromQuiz">(No skin type provided. Please go back to Quiz.)</p>

    <button
      type="button"
      (click)="onGetRecommendations()"
      [disabled]="loading"
    >
      {{ loading ? 'Loading…' : 'Show Results' }}
    </button>
  </div>

  <!-- מוצרים אחרים -->
  <div *ngIf="selectedProduct !== 'primer'">
    <div *ngIf="questions && questions.length > 0; else noQuestions">
      <div *ngFor="let question of questions; index as i">
        <label [for]="'question' + i">
          {{ i + 1 }}. {{ question.text }}
        </label>

        <select
          *ngIf="question.options && question.options.length > 0"
          [id]="'question' + i"
          [(ngModel)]="answers[i]"
          [name]="'q' + i"
        >
          <option [ngValue]="">Select an option</option>
          <option *ngFor="let option of question.options" [ngValue]="option">
            {{ option }}
          </option>
        </select>

        <textarea
          *ngIf="!question.options || question.options.length === 0"
          [id]="'question' + i"
          [(ngModel)]="answers[i]"
          [name]="'q' + i"
          placeholder="Type your answer here..."
        ></textarea>
      </div>

      <button
        type="button"
        (click)="onGetRecommendations()"
        [disabled]="loading"
      >
        {{ loading ? 'Loading…' : 'Show Results' }}
      </button>
    </div>

    <ng-template #noQuestions>
      <p>No questions available for this product.</p>
    </ng-template>
  </div>
</div>
