import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  title = 'Welcome to MatchMyBeauty';
  isLoggedIn = false;

  constructor(private userService: UserService, private router: Router) {}

  ngOnInit(): void {
    const username = this.userService.getLoggedInUser();
    this.isLoggedIn = !!username;
  }

  logout(): void {
    this.userService.logout();
    this.isLoggedIn = false;
    this.router.navigate(['/register']); 
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Home page</title>
    <link rel="stylesheet" href="home.component.css">
</head>
<body>
  <header>
    <a [routerLink]="['/']">
        <img src="assets/logo.jpg" alt="Logo" class="logo">
    </a>
  </header>

  <div class="home">
    <h1>{{ title }}</h1>
    <button routerLink="/quiz" class="start-button">START HERE ></button>
    <button routerLink="/profile" class="profile-button">Profile</button>
    <button *ngIf="isLoggedIn" (click)="logout()" class="logout-button">Logout</button>
  </div>
</body>
</html>
